import React, { useContext, useEffect, useState } from "react";
import { Context } from "../hooks/context.js";
import axios from "axios";
import { Card, Button } from "react-bootstrap";
import DatePicker from "react-datepicker";

import "bootstrap/dist/css/bootstrap.min.css";
import "react-datepicker/dist/react-datepicker.css";

const Principal = () => {
  const [context, setContext] = useContext(Context);
  const [eventos, setEventos] = useState([]);
  const [edit, setEdit] = useState(false);
  const [eventoActual, setEventoActual] = useState(0);
  const [startDate, setStartDate] = useState(new Date());
  const [endDate, setEndDate] = useState(new Date());
  const [nombre, setNombre] = useState("");
  const [categoria, setCategoria] = useState("");
  const [lugar, setLugar] = useState("");
  const [direccion, setDireccion] = useState("");
  const [tipo, setTipo] = useState("");

  const user = context;
  useEffect(() => {
    eventosUsuario();
  }, [eventos]);
  const eventosUsuario = () => {
    axios.get("/usuarios/" + user.id + "/eventos").then((response) => {
      //Info en data
      setEventos(response.data);
    });
  };

  const eliminar = (idEvento, idCreador) => {
    axios.delete("/usuarios/" + idCreador + "/eventos/" + idEvento);
  };
  const handleSubmit = async (e) => {
    console.log(startDate);
    e.preventDefault();
    const response = await axios.put(
      "/usuarios/" + user.id + "/eventos/" + eventoActual,
      {
        nombre: nombre,
        categoria: categoria,
        lugar: lugar,
        direccion: direccion,
        fechaInicio: startDate,
        fechaFin: endDate,
        tipo: tipo,
      },
      { headers: { "Content-Type": "application/json" } }
    );
  };
  const editar = (idEvento) => {
    return (
      <div>
        <h2>Editando evento</h2>
        <form onSubmit={handleSubmit}>
          <label htmlFor="nombre">Nombre: </label>
          <input
            type="text"
            placeholder="Ingrese el nuevo nombre del evento"
            onChange={(nombre) => {
              setNombre(nombre);
            }}
          />
          <div>
            <label htmlFor="direccion">Direccion: </label>
            <input
              type="text"
              placeholder="Ingrese la nueva direccion del evento"
              onChange={(direccion) => {
                setDireccion(direccion);
              }}
            />
          </div>
          <div>
            <label htmlFor="categoria">Categoria: </label>
            <input
              type="text"
              placeholder="Ingrese la nueva categoria del evento"รง
              onChange={(categoria) => {
                setCategoria(categoria);
              }}
            />
          </div>
          <div>
            <label htmlFor="lugar">Lugar: </label>
            <input
              type="password"
              placeholder="Ingrese le nuevo lugar del evento"
              onChange={(lugar) => {
                setLugar(lugar);
              }}
            />
          </div>
          <div>
            <label htmlFor="fechaInicio">Fecha Inicio: </label>
            <DatePicker
              selected={startDate}
              format={"yyyy-mm-dd hh:mm:ss"}
              onChange={(date) => {
                setStartDate(date);
              }}
            />
          </div>
          <div>
            <label htmlFor="password">FechaFin: </label>
            <DatePicker
              selected={endDate}
              format={"yyyy-mm-dd hh:mm:ss"}
              onChange={(date) => {
                setEndDate(date);
              }}
            />{" "}
          </div>
          <div>
            <label htmlFor="tipo">Tipo: </label>
            <input type="text" placeholder="Ingrese el tipo del evento" 
            onChange={(tipo) => {
              setTipo(tipo);
            }}/>
          </div>
          <button type="submit">Editar evento</button>
        </form>
      </div>
    );
  };
  return (
    <div className="container">
      {eventos.map((evento, index) => (
        <div key={index + 1}>
          <Card style={{ width: "18rem" }}>
            <Card.Body>
              <Card.Title>{evento.nombre}</Card.Title>
              <Card.Subtitle className="mb-2 text-muted">
                {evento.direccion} - {evento.lugar}
              </Card.Subtitle>
              <Card.Text>
                <p>
                  Categoria: {evento.categoria}
                  <br></br>
                  Empieza: {evento.fechaInicio}
                  <br></br>
                  Termina: {evento.fechaFin}
                  <br></br>
                  Tipo: {evento.tipo}
                </p>
              </Card.Text>
              <Card.Link
                onClick={() => {
                  setEdit(true);
                  setEventoActual(evento.id);
                }}
              >
                Editar evento
              </Card.Link>
              <Card.Link onClick={() => eliminar(evento.id, evento.creador_id)}>
                Eliminar evento
              </Card.Link>
            </Card.Body>
          </Card>
        </div>
      ))}
      <div className="col-6"></div>
      {edit ? editar(eventoActual) : null}
    </div>
  );
};
export default Principal;
