import React, { useContext, useEffect, useState } from "react";
import { Context } from "../hooks/context.js";
import axios from "axios";
import { Card, Button } from "react-bootstrap";
import DatePicker from "react-datepicker";

import "bootstrap/dist/css/bootstrap.min.css";
import "react-datepicker/dist/react-datepicker.css";

const Principal = () => {
  const [context, setContext] = useContext(Context);
  const [eventos, setEventos] = useState([]);
  const [edit, setEdit] = useState(false);
  const [eventoActual, setEventoActual] = useState(0);
  const [startDate, setStartDate] = useState(new Date());
  const [endDate, setEndDate] = useState(new Date());
  const [nombre, setNombre] = useState("");
  const [categoria, setCategoria] = useState("");
  const [lugar, setLugar] = useState("");
  const [direccion, setDireccion] = useState("");
  const [tipo, setTipo] = useState("");
  const [post, setPost] = useState(false);

  const user = context;

  useEffect(() => {
    eventosUsuario();
  }, []); //hay que colocar eventos en []

  const eventosUsuario = () => {
    axios.get("/usuarios/" + user.id + "/eventos").then((response) => {
      //Info en data
      setEventos(response.data);
    });
  };

  const eliminar = (idEvento, idCreador) => {
    axios.delete("/usuarios/" + idCreador + "/eventos/" + idEvento);
  };
  const handleSubmit = async (e) => {
    e.preventDefault();
    const response = await axios.put(
      "/usuarios/" + user.id + "/eventos/" + eventoActual,
      {
        nombre: nombre,
        categoria: categoria,
        lugar: lugar,
        direccion: direccion,
        fechaInicio: startDate,
        fechaFin: endDate,
        tipo: tipo,
      },
      { headers: { "Content-Type": "application/json" } }
    );
    if (response.status === 200) {
      alert("Se actualizó el evento correctamente");
    }
    if (!response.status) {
      alert(
        "No se pudo actualizar el evento, revise el valor de los parametros"
      );
    }
    setEdit(false);
    setEventoActual(0);
  };
  const editar = (idEvento) => {
    return (
      <div>
        <h2>Editando evento</h2>
        <form onSubmit={handleSubmit}>
          <label htmlFor="nombre">Nombre: </label>
          <input
            type="text"
            value={nombre}
            placeholder="Ingrese el nuevo nombre del evento"
            onChange={({ target }) => setNombre(target.value)}
          />
          <div>
            <label htmlFor="direccion">Direccion: </label>
            <input
              type="text"
              value={direccion}
              placeholder="Ingrese la nueva direccion del evento"
              onChange={({ target }) => setDireccion(target.value)}
            />
          </div>
          <div>
            <label htmlFor="categoria">Categoria: </label>
            <input
              type="text"
              value={categoria}
              placeholder="Ingrese la nueva categoria del evento"
              onChange={({ target }) => setCategoria(target.value)}
            />
          </div>
          <div>
            <label htmlFor="lugar">Lugar: </label>
            <input
              type="text"
              value={lugar}
              placeholder="Ingrese le nuevo lugar del evento"
              onChange={({ target }) => setLugar(target.value)}
            />
          </div>
          <div>
            <label htmlFor="fechaInicio">Fecha Inicio: </label>
            <DatePicker
              selected={startDate}
              format={"yyyy-mm-dd hh:mm:ss"}
              onChange={(date) => {
                setStartDate(date);
              }}
            />
          </div>
          <div>
            <label htmlFor="password">FechaFin: </label>
            <DatePicker
              selected={endDate}
              format={"yyyy-mm-dd hh:mm:ss"}
              onChange={(date) => {
                setEndDate(date);
              }}
            />{" "}
          </div>
          <div>
            <label htmlFor="tipo">Tipo: </label>
            <input
              type="text"
              value={tipo}
              placeholder="Ingrese el tipo del evento"
              onChange={({ target }) => setTipo(target.value)}
            />
          </div>
          <button type="submit">Editar evento</button>
        </form>
      </div>
    );
  };
  const handleSubmitPost = async (e) => {
    e.preventDefault();
    const response = await axios.post(
      "/usuarios/" + user.id + "/eventos",
      {
        nombre: nombre,
        categoria: categoria,
        lugar: lugar,
        direccion: direccion,
        fechaInicio: startDate,
        fechaFin: endDate,
        tipo: tipo,
      },
      { headers: { "Content-Type": "application/json" } }
    );
    if (response.status === 200) {
      alert("Se creo el evento correctamente");
    }
    if (!response.status) {
      alert(
        "No se pudo actualizar el evento, revise el valor de los parametros"
      );
    }
    setPost(false);
  };
  const agregar = () => {
    return (
      <div>
        <h2>Agregando evento</h2>
        <form onSubmit={handleSubmitPost}>
          <label htmlFor="nombre">Nombre: </label>
          <input
            type="text"
            value={nombre}
            placeholder="Ingrese el nuevo nombre del evento"
            onChange={({ target }) => setNombre(target.value)}
          />
          <div>
            <label htmlFor="direccion">Direccion: </label>
            <input
              type="text"
              value={direccion}
              placeholder="Ingrese la nueva direccion del evento"
              onChange={({ target }) => setDireccion(target.value)}
            />
          </div>
          <div>
            <label htmlFor="categoria">Categoria: </label>
            <input
              type="text"
              value={categoria}
              placeholder="Ingrese la nueva categoria del evento"
              onChange={({ target }) => setCategoria(target.value)}
            />
          </div>
          <div>
            <label htmlFor="lugar">Lugar: </label>
            <input
              type="text"
              value={lugar}
              placeholder="Ingrese le nuevo lugar del evento"
              onChange={({ target }) => setLugar(target.value)}
            />
          </div>
          <div>
            <label htmlFor="fechaInicio">Fecha Inicio: </label>
            <DatePicker
              selected={startDate}
              format={"yyyy-mm-dd hh:mm:ss"}
              onChange={(date) => {
                setStartDate(date);
              }}
            />
          </div>
          <div>
            <label htmlFor="password">FechaFin: </label>
            <DatePicker
              selected={endDate}
              format={"yyyy-mm-dd hh:mm:ss"}
              onChange={(date) => {
                setEndDate(date);
              }}
            />{" "}
          </div>
          <div>
            <label htmlFor="tipo">Tipo: </label>
            <input
              type="text"
              value={tipo}
              placeholder="Ingrese el tipo del evento"
              onChange={({ target }) => setTipo(target.value)}
            />
          </div>
          <button type="submit">Agregar evento</button>
        </form>
      </div>
    );
  };
  return (
    <div className="container">
      <Button
        onClick={() => {
          setPost(true);
        }}
      >
        Agregar un evento
      </Button>
      {eventos.map((evento, index) => (
        <div key={index + 1}>
          <Card style={{ width: "18rem" }}>
            <Card.Body>
              <Card.Title>{evento.nombre}</Card.Title>
              <Card.Subtitle className="mb-2 text-muted">
                {evento.direccion} - {evento.lugar}
              </Card.Subtitle>
              <Card.Text>
                <div className="row">
                  <p>Categoria: {evento.categoria}</p>
                </div>
                <div className="row">
                  <p>Empieza: {evento.fechaInicio}</p>
                </div>
                <div className="row">
                  <p>Termina: {evento.fechaFin}</p>
                </div>
                <div className="row">
                  <p>Tipo: {evento.tipo}}</p>
                </div>
              </Card.Text>
              <Card.Link
                onClick={() => {
                  setEdit(true);
                  setEventoActual(evento.id);
                }}
              >
                Editar evento
              </Card.Link>
              <Card.Link onClick={() => eliminar(evento.id, evento.creador_id)}>
                Eliminar evento
              </Card.Link>
            </Card.Body>
          </Card>
        </div>
      ))}
      <div className="col-6"></div>
      {edit ? editar(eventoActual) : null}
      {post ? agregar() : null}
    </div>
  );
};
export default Principal;
